pragma solidity ^0.5.0;

contract ticketingSystem {

    struct Artist
    {
        bytes32 name;
        address owner;
        int artistCategory;
        int ticketSold;
    }

    uint artistId = 1;
     
    mapping (uint => Artist) public artistRegister;

    function creatArtist(bytes32 _name, int _artistCategory, int _ticketSold ) public {
        artistRegister[artistId].name = _name;
        artistRegister[artistId].artistCategory = _artistCategory;
        artistRegister[artistId].ticketSold = _ticketSold;
        artistId += 1;
    }

    function modifyArtist(uint _artistId, bytes32 _newName, int _newArtistCategory, int _newTicketSold) public {
        
        require (msg.sender ==  artistRegister[_artistId].owner);
        artistRegister[_artistId].name = _newName;
        artistRegister[_artistId].artistCategory = _newArtistCategory;
        artistRegister[_artistId].ticketSold = _newTicketSold;
    }

}
